// src/components/Header.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Typed from 'typed.js';
import './styles1.css';
import index from './images/indexbanner.jpg';

const Header = () => {
  const [navLinksVisible, setNavLinksVisible] = useState(false);

  const showMenu = () => {
    setNavLinksVisible(true);
  };

  const hideMenu = () => {
    setNavLinksVisible(false);
  };

  useEffect(() => {
    // Add your Typed.js initialization here
    const typed = new Typed(".auto-type", {
      strings: ["Learning", "Coding", "Sleeping", "Eating"],
      typeSpeed: 150,
      backSpeed: 150,
      loop: true,
    });

    // Cleanup Typed.js on component unmount
    return () => {
      typed.destroy();
    };
  }, []);

  return (
    <div id="header1">
      <section className="header">
        <img src={index} className="back-video" alt="Background Video" />
        <nav>
          {/* ... your navigation code */}
        </nav>
        <div className="text-box">
          {/* ... your text box code */}
        </div>

        {/* Project Sections */}
        <section className="hidden">
          <img src="images/unity3.png" className="neon2" alt="Bok Choy" />
          <h11>Bok Choy</h11>
          <p style={{ textAlign: 'left', position: 'relative', left: '50px', bottom: '225px', fontSize: '15px' }}>
            In the adventure game Bok Choy Adventure, players will follow Amara,
            a young adventurer lost on a mysterious and visually stunning island
            after a deadly shipwreck. The game delivers a truly engaging experience,
            immersing players in a rich and dynamic world filled with exciting secrets
            waiting to be revealed.
          </p>
        </section>

        <section className="hidden">
          <img src="images/neonnights.png" className="neon" alt="Neon Nights" />
          <h10>Neon Nights</h10>
          <p style={{ textAlign: 'left', position: 'relative', left: '920px', bottom: '325px' }}>
            The game is a cyberpunk RPG set in a dystopian future
            where players take on the role of a street-wise hacker
            trying to survive in a world of corrupt corporations,
            ruthless gangs, and high-tech weaponry. The game's UI
            should be designed to create a sense of urban grittiness
            and high-tech innovation, with a sleek and stylized design.
          </p>
        </section>

        {/* Add similar blocks for other project sections */}

      </section>
    </div>
  );
};

export default Header;
