import React from 'react';
import './gallery.css'; // Import CSS for the gallery layout
import echomain from './images/echomainmenu.png'

// Example image array
const images = [
  { id: 1, src: echomain, title:'Echoes of The Abyss'},
  { id: 2, src: './images/scene2.jpg', title: 'Scene 2' },
  { id: 3, src: './images/scene3.jpg', title: 'Scene 3' },
  { id: 4, src: './images/scene4.jpg', title: 'Scene 4' },
  { id: 5, src: './images/scene5.jpg', title: 'Scene 5' },
];

const Gallery = () => {
  return (
    <div className="gallery-container">
      {images.map((image) => (
        <div key={image.id} className="gallery-item">
          <img src={image.src} alt={image.title} className="gallery-image" />
          <div className="image-title">{image.title}</div>
        </div>
      ))}
    </div>
  );
};

export default Gallery;
