// src/components/ProjectsSection.js
import React from 'react';
import './styles1.css';
import unity3 from './images/unity3.png';
import neon from './images/neonnights.png';
import mobileapp from './images/mobileapp.png';
import vrgame8 from './images/vrgame8.png';
import equipmaster2 from './images/autoequip2.png';
import echoboss2 from './images/echoboss2.png';
import echowebsite from './images/gamewebsite.png'

// Project data
const projects = [
  {
    image: echowebsite,
    title: 'Game Website',
    /*description:
      'Website with updates and news about my 2D roguelike game Echoes Of The Abyss.', */
  },
  {
    image: echoboss2,
    title: 'Echoes of the Abyss',
    /*description:
      '2D roguelike dungeon crawler where an adventurer uncovers dark secrets in a mysterious abyss. (In development)',*/
  },
  {
    image: equipmaster2,
    title: 'Equipmaster',
    /*description:
      'Addon to automate gear equipping in a game. Scans inventory and equips the highest item level gear automatically.',*/
  },
  {
    image: vrgame8,
    title: 'BuildaPCVR',
    /*description:
      'VR PC-building simulator providing an engaging experience to learn assembling a PC with lifelike components and guides.',*/
  },
  {
    image: unity3,
    title: 'Bok Choy',
    /*description:
      'Adventure game featuring Amara, a young adventurer exploring a mysterious island filled with secrets after a shipwreck.',*/
  },
  {
    image: neon,
    title: 'Neon Nights',
    /*description:
      'Cyberpunk RPG set in a dystopian future where players navigate a gritty, high-tech world of hackers, gangs, and corporations.',*/
  },
  {
    image: mobileapp,
    title: 'EcoConnect',
   /* description:
      'Mobile app promoting sustainable living by connecting individuals in local communities to encourage eco-friendly practices.',*/
  },
];

const ProjectsSection = () => {
  return (
    <section className="projects-section">
      <div className="projects-grid">
        {projects.map((project, index) => (
          <div className="project" key={index}>
            <img src={project.image} alt={project.title} />
            <div className="project-details">
              <h2>{project.title}</h2>
              <p>{project.description}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default ProjectsSection;
