// src/components/ProjectsSection.js
import React from 'react';
import './styles1.css';
import unity3 from './images/unity3.png';
import neon from './images/neonnights.png';
import mobileapp from './images/mobileapp.png';
import vrgame8 from './images/vrgame8.png';
import equipmaster2 from './images/autoequip2.png';
import echoboss2 from './images/echoboss2.png';
import echowebsite from './images/gamewebsite.png'
import aerospace from './images/aerospace.png'

// Project data
const projects = [
  {
    image: echowebsite,
    title: 'Game Website',
    description:
      'Website for my 2d Roguelike dungeon crawler.',
  },
  {
    image: echoboss2,
    title: 'Echoes of the Abyss',
    description:
      '2D roguelike dungeon crawler (In development)',
  },
  {
    image: equipmaster2,
    title: 'Equipmaster',
    description:
      'Addon to autoequip the highest item level in game.',
  },
  {
    image: aerospace,
    title: 'DOP Map',
    description:
      'Full Stack web application to display dop data.',
  },
  {
    image: vrgame8,
    title: 'BuildaPCVR',
    description:
      'VR PC-building simulator.',
  },
  {
    image: unity3,
    title: 'Bok Choy',
    description:
      'Open World MMORPG with bosses and more!',
  },
  {
    image: neon,
    title: 'Neon Nights',
    description:
      'Figma framwork Design Idea for a game.',
  },
  {
    image: mobileapp,
    title: 'EcoConnect',
    description:
      'Mobile app design idea using Figma.',
  },
];

const ProjectsSection = () => {
  return (
    <section className="projects-section">
      <div className="projects-grid">
        {projects.map((project, index) => (
          <div className="project" key={index}>
            <img src={project.image} alt={project.title} />
            <div className="project-details">
              <h2>{project.title}</h2>
              <p>{project.description}</p>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default ProjectsSection;
